var debug=true;var output=window.console;var _NoError={code:"0",string:"No Error",diagnostic:"No Error"};var _GeneralException={code:"101",string:"General Exception",diagnostic:"General Exception"};var initialized=false;var apiHandle=null;function doLMSInitialize(){if(initialized){return"true"}var a=getAPIHandle();if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSInitialize was not successful.");return"false"}var c=a.LMSInitialize("");if(c.toString()!="true"){var b=ErrorHandler();message("LMSInitialize failed with error code: "+b.code)}else{initialized=true}return c.toString()}function doLMSFinish(){if(!initialized){return"true"}var a=getAPIHandle();if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSFinish was not successful.");return"false"}else{var c=a.LMSFinish("");if(c.toString()!="true"){var b=ErrorHandler();message("LMSFinish failed with error code: "+b.code)}}initialized=false;return c.toString()}function doLMSGetValue(d){var a=getAPIHandle();var e="";if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSGetValue was not successful.")}else{if(!initialized&&!doLMSInitialize()){var b=ErrorHandler();message("LMSGetValue failed - Could not initialize communication with the LMS - error code: "+b.code)}else{e=a.LMSGetValue(d);var c=ErrorHandler();if(c.code!=_NoError.code){message("LMSGetValue("+d+") failed. \n"+c.code+": "+c.string);e=""}}}return e.toString()}function doLMSSetValue(c,e){var a=getAPIHandle();var d="false";if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSSetValue was not successful.")}else{if(!initialized&&!doLMSInitialize()){var b=ErrorHandler();message("LMSSetValue failed - Could not initialize communication with the LMS - error code: "+b.code)}else{d=a.LMSSetValue(c,e);if(d.toString()!="true"){var b=ErrorHandler();message("LMSSetValue("+c+", "+e+") failed. \n"+b.code+": "+b.string)}}}return d.toString()}function doLMSCommit(){var a=getAPIHandle();var c="false";if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSCommit was not successful.")}else{if(!initialized&&!doLMSInitialize()){var b=ErrorHandler();message("LMSCommit failed - Could not initialize communication with the LMS - error code: "+b.code)}else{c=a.LMSCommit("");if(c!="true"){var b=ErrorHandler();message("LMSCommit failed - error code: "+b.code)}}}return c.toString()}function doLMSGetLastError(){var a=getAPIHandle();if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSGetLastError was not successful.");return _GeneralException.code}return a.LMSGetLastError().toString()}function doLMSGetErrorString(b){var a=getAPIHandle();if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSGetErrorString was not successful.");return _GeneralException.string}return a.LMSGetErrorString(b).toString()}function doLMSGetDiagnostic(b){var a=getAPIHandle();if(a==null){message("Unable to locate the LMS's API Implementation.\nLMSGetDiagnostic was not successful.");return"Unable to locate the LMS's API Implementation. LMSGetDiagnostic was not successful."}return a.LMSGetDiagnostic(b).toString()}function ErrorHandler(){var b={code:_NoError.code,string:_NoError.string,diagnostic:_NoError.diagnostic};var a=getAPIHandle();if(a==null){message("Unable to locate the LMS's API Implementation.\nCannot determine LMS error code.");b.code=_GeneralException.code;b.string=_GeneralException.string;b.diagnostic="Unable to locate the LMS's API Implementation. Cannot determine LMS error code.";return b}b.code=a.LMSGetLastError().toString();if(b.code!=_NoError.code){b.string=a.LMSGetErrorString(b.code);b.diagnostic=a.LMSGetDiagnostic("")}return b}function getAPIHandle(){if(apiHandle==null){apiHandle=getAPI()}return apiHandle}function findAPI(b){var a=0;while((b.API==null)&&(b.parent!=null)&&(b.parent!=b)){a++;if(a>7){message("Error finding API -- too deeply nested.");return null}b=b.parent}return b.API}function getAPI(){var a=findAPI(window);if((a==null)&&(window.opener!=null)&&(typeof(window.opener)!="undefined")){a=findAPI(window.opener)}if(a==null){message("Unable to find an API adapter")}return a}function message(a){if(debug){output.log(a)}};